import { AfterViewInit, Component, EventEmitter, Input, Output, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Supplier } from 'src/app/Model/Supplier';

@Component({
  selector: 'app-create-supplier-card',
  templateUrl: './create-supplier-card.component.html',
  styleUrls: ['./create-supplier-card.component.css']
})
export class createSupplierCardComponent implements AfterViewInit{
 @Input() isEditMode: boolean = false;
 @Input() selectedSupplier: Supplier;

 @ViewChild('supplierForm') supplierForm: NgForm;
  @Output()
  closeForm: EventEmitter<boolean> = new EventEmitter<boolean>();

  @Output() emitSupplierData:EventEmitter<Supplier> = new EventEmitter<Supplier>();

  ngAfterViewInit(): void {
    setTimeout(() => {
      this.supplierForm.form.patchValue(this.selectedSupplier);
    }, 0)
  }

  OnCloseForm(){
    this.closeForm.emit(false);
  }

  onSupplierFormSubmitted(supplierForm: NgForm) {
    this.emitSupplierData.emit(supplierForm.value);
    this.closeForm.emit(false);
  }
}
<div class="overlay">
	<div class="popup">
		<h2>{{isEditMode ? 'Uredi dostavljaca' : 'Kreiraj dostavljaca'}}</h2>
		<a class="close" routerLink="supplier" (click)="OnCloseForm()">&times;</a>
		<div class="content">
			<section class="container">
                <form action="#" class="form" #supplierForm="ngForm" (ngSubmit)="onSupplierFormSubmitted(supplierForm)">
                  <div class="column">
                    <div class="input-box">
                      <label>Name</label>
                      <input type="text" required name="supplierName" ngModel #name="ngModel"/>
                      <small *ngIf="name.invalid && name.touched">Morate popuniti ovo polje</small>
                    </div>
                    <div class="input-box">
                      <label>Surname</label>
                      <input type="text" required name="supplierSurname" ngModel/>
                    </div>
                  </div>

                  <div class="column">
                    <div class="input-box">
                      <label>Phone number</label>
                      <input type="number" required name="supplierPhoneNumber" ngModel/>
                    </div>
                    <div class="input-box">
                      <label>Datum dolaska</label>
                      <input type="date" required name="supplierDateOfArrival" ngModel/>
                    </div>
                  </div>

                  <div class="column">
                    <div class="input-box">
                      <label>Adresa polaska</label>
                      <input type="text" required name="supplierBeginDestination" ngModel/>
                    </div>
                    <div class="input-box">
                      <label>Konacna adresa</label>
                      <input type="text" required name="supplierFinalDestination" ngModel/>
                    </div>
                  </div>
                  <div class="input-box address">
                    <div class="column">
                      <div class="select-box">
                        <select name="supplierStatus" ngModel>
                          <option value="" disabled selected hidden>Priority</option>
                          <option hidden>Priority</option>
                          <option value="low">Low</option>
                          <option value="medium">Medium</option>
                          <option value="high">High</option>
                          <option value="critical">Critical</option>
                        </select>
                      </div>
                      <div class="select-box">
                        <select name="supplierPriority" ngModel>
                          <option value="" disabled selected hidden>Status</option>
                          <option hidden>Status</option>
                          <option value="open">Open</option>
                          <option value="started">Started</option>
                          <option value="in-progress">In Progress</option>
                          <option value="complete">Complete</option>
                        </select>
                      </div>
                    </div>
                  </div>
                  <button type="submit">{{isEditMode ? 'Uredi' : 'Nastavi'}}</button>
                </form>
              </section>
		</div>
	</div>
</div>
