import { AfterViewInit, Component, EventEmitter, Input, Output, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Product } from 'src/app/Model/Product';

@Component({
  selector: 'app-create-product-card',
  templateUrl: './create-product-card.component.html',
  styleUrls: ['./create-product-card.component.css']
})
export class CreateProductCardComponent implements AfterViewInit{
@Input() isEditMode:boolean = false;
@Input() selectedProduct:Product;

@ViewChild('productForm') productForm:NgForm;
@Output()
 closeForm: EventEmitter<boolean> = new EventEmitter<boolean>();

@Output() 
emitProductData: EventEmitter<Product> = new EventEmitter<Product>();

ngAfterViewInit(): void {
  setTimeout(() => {
    this.productForm.form.patchValue(this.selectedProduct)
  }, 0)
}
onCloseForm(){
  this.closeForm.emit(false)
}

onProductFormSubmitted(productForm: NgForm) {
  if (productForm.valid) {
    this.closeForm.emit(false);
    this.emitProductData.emit(productForm.value);
    console.log('Form submitted:', productForm.value);
  } else {
    alert('Form is invalid');
  }
}

get buttonClass() {
  return this.productForm?.valid ? 'enabled' : 'disabled';
}
}
<div class="overlay">
	<div class="popup">
		<h2>kreiraj proizvod</h2>
    <small>Molimo popunite sva polja</small>
		<a class="close" routerLink="products" (click)="onCloseForm()">&times;</a>
		<div class="content">
			<section class="container">
                <form action="#" class="form" #productForm="ngForm" (ngSubmit)="onProductFormSubmitted(productForm)">
                  <div class="column">
                    <div class="input-box">
                      <label>Name of product</label>
                      <input type="text" required name="productName" ngModel #pName="ngModel"/>
                      <div>
                        <small *ngIf="pName.invalid && pName.touched">Morate popuniti ovo polje</small>
                      </div>
                    </div>
                    <div class="input-box">
                      <label>Type of product</label>
                      <input type="text" required name="productType" ngModel #topName="ngModel"/>
                      <div>
                        <small *ngIf="topName.invalid && topName.touched">Morate popuniti ovo polje</small>
                      </div>
                    </div>
                  </div>

                  <div class="column">
                    <div class="input-box">
                      <label>Class(quality grade)</label>
                      <input type="text" required name="productClass" ngModel #cName="ngModel"/>
                      <div>
                        <small *ngIf="cName.invalid && cName.touched">Morate popuniti ovo polje</small>
                      </div>
                    </div>
                    <div class="input-box">
                      <label>Date of entry</label>
                      <input type="date" required name="productDateOfEntry" ngModel #doeName="ngModel"/>
                      <div>
                        <small *ngIf="doeName.invalid && doeName.touched">Morate popuniti ovo polje</small>
                      </div>
                    </div>
                  </div>

                  <div class="column">
                    <div class="input-box">
                      <label>Expiry date</label>
                      <input type="date" required name="productExpiryDate" ngModel #eName="ngModel"/>
                      <div>
                        <small *ngIf="eName.invalid && eName.touched">Morate popuniti ovo polje</small>
                      </div>
                    </div>

                  </div>
                  <div class="input-box address">
                    <div class="column">
                      <div class="select-box">
                        <select name="productStatus" ngModel #pStatusName="ngModel">
                          <option value="" disabled selected hidden>Priority</option>
                          <option hidden>Priority</option>
                          <option value="low">Low</option>
                          <option value="medium">Medium</option>
                          <option value="high">High</option>
                          <option value="critical">Critical</option>
                        </select>
                        <div>
                          <small *ngIf="pStatusName.invalid && pStatusName.touched">Morate popuniti ovo polje</small>
                        </div>
                      </div>
                      <div class="select-box">
                        <select name="productPriority" ngModel #productPriorityName="ngModel">
                          <option value="" disabled selected hidden>Status</option>
                          <option value="open">Open</option>
                          <option value="started">Started</option>
                          <option value="in-progress">In Progress</option>
                          <option value="complete">Complete</option>
                        </select>
                        <div>
                          <small *ngIf="productPriorityName.invalid && productPriorityName.touched">Morate popuniti ovo polje</small>
                        </div>
                      </div>
                    </div>
                  </div>
                  <button type="submit" [disabled]="!productForm.valid">nastavi</button>
                </form>
              </section>
		</div>
	</div>
</div>
