import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Product } from 'src/app/Model/Product';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent {
@Output()
closeDetailView: EventEmitter<boolean> = new EventEmitter<boolean>();

@Input() currentProduct: Product | null = null;

onCloseProductDetail(){
  this.closeDetailView.emit(false)
}
}
<div class="overlay">
	<div class="popup">
        <h2>{{ currentProduct?.productName }}</h2>
        <hr>
		<a class="close" href="#" (click)="onCloseProductDetail()">&times;</a>
		<div class="content">
			<section class="container">
                <div>
                    <p>
                        {{ currentProduct?.productName }}
                    </p>
                </div>
                <div>
                    <div>
                        <strong>Tip: </strong>
                        {{ currentProduct?.productType }}
                    </div>
                    <div>
                        <strong>Klasa: </strong>
                        {{ currentProduct?.productClass }} 
                    </div>
                </div>
                <div>
                    <div>
                        <strong>Datum primanja: </strong>
                        {{ currentProduct?.productDateOfEntry }}
                    </div>
                    <div>
                        <strong>Datum isteka: </strong>
                        {{ currentProduct?.productExpiryDate }} 
                    </div>

                    <div>
                        <strong>prioritet: </strong>
                        {{ currentProduct?.productPriority }} 
                    </div>

                    <div>
                        <strong>Status: </strong>
                        {{ currentProduct?.productStatus }} 
                    </div>
                </div>
            </section>
		</div>
	</div>
</div>
