import { Component, OnInit, inject } from '@angular/core';
import { Supplier } from '../Model/Supplier';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent{
  showCreateSupplierForm: boolean = false;
  http:HttpClient = inject(HttpClient)

  OpenCreateSupplierForm(){
    this.showCreateSupplierForm = true;
  }

  CloseCreateSupplierForm(){
    this.showCreateSupplierForm = false;
  }

  createSupplier(data: Supplier) {
    const headers = new HttpHeaders({'supplier':'new-supplier'})
    this.http.post<{name:string}>('https://fruitmagacine-default-rtdb.firebaseio.com/suppliers.json', data, {headers:headers}).subscribe((response) => {
      console.log(response);
    })
  }

  private fetchAllTasks(data:Supplier){
    this.http.get('https://fruitmagacine-default-rtdb.firebaseio.com/suppliers.json').subscribe(response => console.log(response)
    )
  }
}
<app-create-supplier-card 
        *ngIf="showCreateSupplierForm" 
        (closeForm)="CloseCreateSupplierForm()"
        (emitSupplierData)="createSupplier($event)"
></app-create-supplier-card>

<div class="dashboard-container">
    <div class="dashboard-wrapper">
        <div>
            <h2>Dashboard</h2>
        </div>
        <div>
            <button class="create-task-btn" (click)="OpenCreateSupplierForm()">Create Task</button>
            <button class="create-task-btn">Fetch Tasks</button>
            <button class="create-task-btn">Clear Tasks</button>
        </div>
    </div>

    <hr>

    <div class="dashboard-item-container">
            <div class="task-item">
                <div class="task-title">
                    <h3>Supplier</h3>
                </div>
                <p><b>Assigned to: </b>John Smith</p>
                <p><b>Date of Arrival: </b>John Smith</p>
                <p><b>Begin destination: </b>John Smith</p>
                <p><b>Final destination: </b>John Smith</p>
                <p><b>Priority: </b>High</p>
                <p><b>Status: </b>In Progress</p>
                <div class="task-action-icons">
                    <button class="task-action-btn" title="Detail">
                        <i class="fa fa-info-circle" aria-hidden="true"></i>
                    </button>
                    <button class="task-action-btn" title="Edit">
                        <i class="fa fa-pencil-square-o" aria-hidden="true"></i>
                    </button>
                    <button class="task-action-btn" title="Delete">
                        <i class="fa fa-trash" aria-hidden="true"></i>
                    </button>            
                </div>
            </div>
    </div>
</div>
