import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Supplier } from 'src/app/Model/Supplier';

@Component({
  selector: 'app-supplier-details',
  templateUrl: './supplier-details.component.html',
  styleUrls: ['./supplier-details.component.css']
})
export class SupplierDetailsComponent {
  @Output()
  CloseDetailView: EventEmitter<boolean> = new EventEmitter<boolean>();

  @Input() currentSupplier: Supplier | null = null;;

  OnCloseTaskDetail(){
    this.CloseDetailView.emit(false);
  }
}
<div class="overlay">
	<div class="popup">
        <h2>{{ currentSupplier?.supplierName }}</h2>
        <hr>
		<a class="close" href="#" (click)="OnCloseTaskDetail()">&times;</a>
		<div class="content">
			<section class="container">

                <h3>Description</h3>
                <p>
                    {{ currentSupplier?.supplierName }} {{ currentSupplier?.supplierSurname }}
                </p>
                <div>
                    <div style="width: 50%;">
                        <strong>Date of Arrival: </strong>
                        {{ currentSupplier?.supplierDateOfArrival }}
                    </div>
                    <div style="width: 50%;">
                        <strong>Final destination: </strong>
                        {{ currentSupplier?.supplierFinalDestination }} 
                    </div>
                </div>
                <div>
                    <div style="width: 50%;">
                        <strong>Priority: </strong>
                        {{ currentSupplier?.supplierPriority }}
                    </div>
                    <div style="width: 50%;">
                        <strong>Current Status: </strong>
                        {{ currentSupplier?.supplierStatus }} 
                    </div>
                </div>
            </section>
		</div>
	</div>
</div>
