import { Component, OnInit, inject } from '@angular/core';
import { Supplier } from '../Model/Supplier';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { map } from 'rxjs';
import { SupplierService } from '../services/supplier.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{
  showCreateSupplierForm: boolean = false;
  http:HttpClient = inject(HttpClient);
  allSuppliers: Supplier[] = [];
  supplierService:SupplierService = inject(SupplierService)

  ngOnInit(): void {
    this.fetchAllTasks()
  }

  OpenCreateSupplierForm(){
    this.showCreateSupplierForm = true;
  }

  CloseCreateSupplierForm(){
    this.showCreateSupplierForm = false;
  }

  createSupplier(supplier: Supplier) {
    this.supplierService.createSupplier(supplier)
  }

  private fetchAllTasks() {
    this.http.get<{ [key: string]: Supplier }>('https://fruitmagacine-default-rtdb.firebaseio.com/suppliers.json')
      .pipe(
        map((response) => {
          let suppliers: Supplier[] = [];
          for (let key in response) {
            if (response.hasOwnProperty(key)) {
              suppliers.push({ ...response[key], id: key });
            }
          }
          return suppliers;
        })
      )
      .subscribe((suppliers) => {
        this.allSuppliers = suppliers;
      });
  }

  deleteSupplier(id: string | undefined) {
    this.supplierService.deleteSupplier(id)
  }

  deleteAllSuppliers(){
    this.http.delete('https://fruitmagacine-default-rtdb.firebaseio.com/suppliers.json')
    .subscribe(() => {
      this.fetchAllTasks();
    })
  }
}
<app-create-supplier-card 
        *ngIf="showCreateSupplierForm" 
        (closeForm)="CloseCreateSupplierForm()"
        (emitSupplierData)="createSupplier($event)"
></app-create-supplier-card>

<div class="dashboard-container">
    <div class="dashboard-wrapper">
        <div>
            <h2>Dashboard</h2>
        </div>
        <div>
            <button class="create-supplier-btn" (click)="OpenCreateSupplierForm()">Create Task</button>
            <button class="create-supplier-btn">Fetch Tasks</button>
            <button class="create-supplier-btn" (click)="deleteAllSuppliers()">Clear Tasks</button>
        </div>
    </div>

    <hr>

    <div class="dashboard-item-container">
        <div *ngIf="allSuppliers.length < 1">
            <h2>No suppliers has been created yet!</h2>
        </div>
        <div class="supplier-item" [ngClass]="'supplier-' + supplier.supplierPriority" *ngFor="let supplier of allSuppliers">
          <div class="supplier-title">
            <h3>Supplier</h3>
          </div>
          <p><b>Assigned to: </b>{{supplier.supplierName}} {{supplier.supplierSurname}}</p>
          <p><b>Date of Arrival: </b>{{supplier.supplierDateOfArrival}}</p>
          <p><b>Begin destination: </b>{{supplier.supplierBeginDestination}}</p>
          <p><b>Final destination: </b>{{supplier.supplierFinalDestination}}</p>
          <p><b>Priority: </b>{{supplier.supplierPriority}}</p>
          <p><b>supplierStatus: </b>{{supplier.supplierStatus}}</p>
          <div class="supplier-action-icons">
            <button class="supplier-action-btn" title="Detail">
              <i class="fa fa-info-circle" aria-hidden="true"></i>
            </button>
            <button class="supplier-action-btn" title="Edit">
              <i class="fa fa-pencil-square-o" aria-hidden="true"></i>
            </button>
            <button class="supplier-action-btn" (click)="deleteSupplier(supplier.id)" title="Delete">
              <i class="fa fa-trash" aria-hidden="true"></i>
            </button>
          </div>
        </div>
      </div>
</div>
